mapper.insert:
insert into matchinfo(matchid,type,runtime,startTime) select i.* from(
select #{item.matchinfoVo.matchid} as matchid, #{item.matchinfoVo.type} as type, #{item.matchinfoVo.runtime} as runtime, #{item.matchinfoVo.startTime} as startTime from dual
)i

mapper.select:
select type
from matchinfo
where matchid = #{matchid}

select matchid
from (
    select rownum rn,m.matchid
    from matchinfo m , teammemberinfo tm
    where m.matchid = tm.matchid
    and tm.snickname = #{snickname}
    order by m.starttime desc
)
where rn<=20

select * from( select rownum rn, t.* from ( SELECT tm.*, c.name, c.picture, m.type, m.runtime, m.starttime, winlose FROM teammemberinfo tm, teaminfo t, matchinfo m, champion c WHERE tm.matchid = m.matchid
and m.type = #{type}
AND tm.championid = c.championid and tm.matchid = t.matchid and tm.teamid = t.teamid AND tm.snickname = #{snickname} order by m.STARTTIME desc )t ) where rn<=20
