mapper.select:
select count(rhit)
from reply
where rNum = #{rNum}

select count(rNohit)
from reply
where rNum = #{rNum}

select * from
 (
   select a.*,rownum rwnum from
   (
	  select * from reply where rwriter=#{username}
	   order by regdate desc
   )a
 )
 where rwnum>=#{startRow} and rwnum<=#{endRow}
